import React from 'react';
import { Box, Image, Heading, Text, Link, Icon } from '@chakra-ui/react';
import { ArrowUpRightSquare } from 'react-icons/fa'; // using react-icons for the arrow icon

export function Project() {
    return (
        <Box
            borderRadius="10px"
            position="relative"
            overflow="hidden"
            _hover={{
                img: {
                    transform: "scale(1.1)",
                },
                div: {
                    height: "100%",
                },
            }}
        >
            <Image
                src="work1.gif"
                borderRadius="10px"
                transition="transform 0.5s"
            />
            <Box
                width="100%"
                height="0"
                background="linear-gradient(rgba(0,0,0,0.8), orangered)"
                borderRadius="10px"
                position="absolute"
                left="0"
                bottom="0"
                overflow="hidden"
                display="flex"
                alignItems="center"
                justifyContent="center"
                flexDirection="column"
                padding="0 40px"
                textAlign="left"
                fontSize="18px"
                marginTop="20px"
                transition="height 0.5s"
            >
                <Heading
                    fontWeight="700"
                    marginBottom="20px"
                    fontSize="26px"
                >
                    This Whole Website!!!
                </Heading>
                <Text>
                    Use HTML and CSS skills with a few simple Javascripts to build the website from craft.
                </Text>
                <Text>
                    Host on Ubuntu installed in AWS E2.
                </Text>
                <Link
                    marginTop="20px"
                    color="orange"
                    textDecoration="none"
                    fontSize="18px"
                    lineHeight="60px"
                    backgroundColor="white"
                    width="60px"
                    height="60px"
                    borderRadius="50%"
                    display="flex"
                    alignItems="center"
                    justifyContent="center"
                    href="#"
                >
                    <Icon as={ArrowUpRightSquare} />
                </Link>
            </Box>
        </Box>
    );
}
