import React from 'react';
import {
  ChakraProvider,
  Box,
  Text,
  Link,
  VStack,
  Code,
  Grid,
  Flex,
  theme,
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';
import { Logo } from './Logo';
import Menu from './menu';
import 'animate.css';

function App() {
  return (
    <ChakraProvider >
      <Box textAlign="center" fontSize="xl">
        <Flex direction="column">
          <Flex>
            <Menu />
            <ColorModeSwitcher justifySelf="flex-end" />
          </Flex>
          <Box height="100vh"
            align="center"
            justify="center"
          >

            <Text class="animate__animated animate__fadeInDown animate__slower" fontSize="6vw">
              Hey, I am
              Ruiqi Wei
            </Text>
            <Text class="animate__animated animate__fadeInLeftBig animate__slower" fontSize="6vw">Welcome to my website !</Text>
          </Box>
        </Flex>
      </Box >
    </ChakraProvider >
  );
}

export default App;
