import React from 'react';
import { Box, Link, Flex, Text, Image } from '@chakra-ui/react';
import AboutImage from '../Assets/About.jpg'


export const About = () => {
    return (
        <Flex height="100vh">
            <Box justify="right" align="right" width="35%" padding="0vw 0vw 0vw 10vw"  >
                <Image src={AboutImage} alt="About" borderRadius="15px" />
            </Box>

            <Box width="65%" paddingLeft="5vw">
                <Text style={{ fontSize: '6vw', color: 'rgb(255,128,0)', fontWeight: '600' }}> About Me</Text>
                <Text>A computer science student at UConn with a passion for programming.
                    Throughout my time learning, I have taken my education out of the classroom, self-educate for Web-Development skills.
                    I am currently looking for software engineering internship opportunities for Summer 2023 or full-time positions after May 2023.
                </Text>

            </Box>

        </Flex>
    )
}
