import React, { useState } from 'react';
import { Box, Link, Flex, Text, Image } from '@chakra-ui/react';
import AboutImage from '../Assets/About.jpg'


export const About = () => {
    return (
        <Flex height="100vh">
            <Box justify="right" align="right" width="35%" padding="2vw 0vw 0vw 10vw"  >
                <Image src={AboutImage} alt="About" borderRadius="15px" />
            </Box>

            <Box width="55%" padding="0 0 0 5vw">
                <Text style={{ fontSize: '5vw', color: 'rgb(255,128,0)', fontWeight: '600' }}> About Me</Text>
                <Text fontSize="md">A computer science student at UConn with a passion for programming.
                    Throughout my time learning, I have taken my education out of the classroom, self-educate for Web-Development skills.
                    I am currently looking for software engineering, full stack web-development internship opportunities.
                </Text>

                <Button />

            </Box>

        </Flex>
    )
}

function Button() {

    const [current, setCurrent] = useState("skill")
    const handleClick = (props) => () => {
        setCurrent(props)
    }

    return (
        <Box>
            <Box display="flex"
                margin="20px 0 40px">
                <Text onClick={handleClick("skill")} marginRight="50px" cursor="pointer" borderBottom={current === 'skill' ? '2px solid red' : ''} transition="border-bottom 0.3s ease-in-out">Skill</Text>
                <Text onClick={handleClick("experience")} marginRight="50px" cursor="pointer" borderBottom={current === 'experience' ? '2px solid red' : ''} transition="border-bottom 0.3s ease-in-out">Experience</Text>
                <Text onClick={handleClick("education")} marginRight="50px" cursor="pointer" borderBottom={current === 'education' ? '2px solid red' : ''} transition="border-bottom 0.3s ease-in-out">Education</Text>
            </Box>
            <Display props={current} />
        </Box >
    )
}


function Display({ props }) {

    if (props == "skill") {
        return (
            <Box id="skills">
                <Text >Base coding language: Python, C++ </Text>
                <Text >Software-Development: Java, Kotlin </Text>
                <Text >Web-Development: Javascript, React, SQL                </Text>

                <ul >
                    <li><i class="fa-brands fa-python" ></i>Python</li>
                    <li><i class="fa-brands fa-codiepie" ></i>C++</li>
                    <li><i class="fa-brands fa-java" ></i>Java</li>
                    <li><i class="fa-brands fa-internet-explorer" ></i>Web-Development</li>
                </ul>
                <ul>
                    <li><i class="fa-brands fa-square-js" ></i>Javascript</li>
                    <li><i class="fa-brands fa-react" ></i>React</li>
                    <li><i class="fa-solid fa-database" ></i>SQL</li>
                    <li><i class="fa-brands fa-aws" ></i>AWS</li>
                </ul>

            </Box>
        )
    }
    else if (props == "experience") {
        return (
            <Box as="ul">
                <Box as="li" style={{ color: 'orangered', marginBottom: '0' }}>Tech Support Center Advanced Student Employee</Box>
                <Box as="li" style={{ color: 'orange', marginTop: '0', marginBottom: '0' }}>UConn, Storrs, CT (Sept.2021-Present)</Box>
                <Box as="li" className="experience_contents">Providing technical support to account issues, computers, classroom support, and more. Troubleshooting over the phone and in person.</Box>

                <Box as="li" style={{ color: 'orangered', marginBottom: '0' }}>Network Engineer Student Assistant</Box>
                <Box as="li" style={{ color: 'orange', marginTop: '0', marginBottom: '0' }}>UConn, Storrs, CT (Sept.2021-Present)</Box>
                <Box as="li" className="experience_contents">Recording, tracking, and reporting network problems, securing work environment and equipment.</Box>
                <Box as="li" className="experience_contents">Help update, modify, and make AutoCAD files with University building information.</Box>

                <Box as="li" style={{ color: 'orangered', marginBottom: '0' }}>Math Tutor</Box>
                <Box as="li" style={{ color: 'orange', marginTop: '0', marginBottom: '0' }}>UConn, Stamford, CT (Sept.2021-Present)</Box>
                <Box as="li" className="experience_contents">Possessed strong math skills and a good understanding of multiple math subjects, such as algebra, geometry, and basic arithmetic. Tutored undergraduate students in related math courses</Box>
            </Box>
        )
    }
    else if (props == "education") {
        return (
            <Box>
                <h1>University of Connecticut</h1>
                <p>GPA: 3.69/4.00</p>
                <p>Honor: Babbidge Scholar</p>
                <h2>Relevant Coursework:</h2>
                <ul>
                    <li>CSE3100: System Programming</li>
                    <li>CSE3150: C++ Essentials</li>
                    <li>CSE3200: Mobile Application Development</li>
                    <li>CSE4701: Principles of Databases</li>
                </ul>
            </Box>
        )
    }

    return (<Text>none</Text>)
}
