import React from 'react';
import { Box, Link, Flex, Text } from '@chakra-ui/react';

const StayMenuItem = ({ children, isLast, to = "/", ...rest }) => {
  return (
    <Link href={to}>
      <Text display="block" {...rest}>
        {children}
      </Text>
    </Link>
  );
};

const Stay = () => {
  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      padding="1.5rem"
    >
      <Flex align="center">
        <Text>
          Stay
        </Text>
      </Flex>

      <Box
        display="flex"
        width="auto"
        alignItems="end"
        flexGrow={1}
      >
        <StayMenuItem to="/">Home</StayMenuItem>
        <StayMenuItem to="/about">About</StayMenuItem>
        <StayMenuItem to="/contact" isLast>Contact</StayMenuItem>
      </Box>
    </Flex>
  );
};

export default Stay;
