import React from 'react';
import {
  ChakraProvider,
  Box,
  Text,
  Link,
  VStack,
  Code,
  Grid,
  Flex,
  theme,
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';
import { Logo } from './Logo';
import Stay from './menu';
import 'animate.css';

function App() {
  return (
    <ChakraProvider >
      <Box textAlign="center" fontSize="xl">
        <Flex direction = "column">
          <Grid>
            <Stay/>
            <ColorModeSwitcher justifySelf="flex-end" />

          </Grid>

          <div class="header_text">
            <p class="animate__animated animate__fadeInDown animate__slower"> Hey, I am <span style = "font-size: 45px; color: rgb(255,128,0);"> Ruiqi Wei</span></p>
            <p class="animate__animated animate__fadeInLeftBig animate__slower">Welcome to my website !</p>


        </div>
        </Flex>
      </Box>
    </ChakraProvider>
  );
}

export default App;
