import React, { useState } from 'react';
import { Button, VStack } from '@chakra-ui/react';

function App() {
  const [selectedButton, setSelectedButton] = useState(null);

  const handleClick = (buttonName) => {
    setSelectedButton(buttonName);
  };

  return (
    <VStack spacing={4}>
      <Button
        colorScheme={selectedButton === 'Button 1' ? 'teal' : 'gray'}
        onClick={() => handleClick('Button 1')}
      >
        Button 1
      </Button>
      <Button
        colorScheme={selectedButton === 'Button 2' ? 'teal' : 'gray'}
        onClick={() => handleClick('Button 2')}
      >
        Button 2
      </Button>
      <Button
        colorScheme={selectedButton === 'Button 3' ? 'teal' : 'gray'}
        onClick={() => handleClick('Button 3')}
      >
        Button 3
      </Button>
    </VStack>
  );
}

export default App;
