import {
  ChakraProvider,
  Box,
  Flex,
} from '@chakra-ui/react';
import { ColorModeSwitcher } from './ColorModeSwitcher';
import Menu from './menu';
import 'animate.css';
import { Hello } from './Compoent/Hello';
import { About } from './Compoent/About';

function App() {
  return (
    <ChakraProvider >
      <Box textAlign="left" fontSize="xl" height="100vh" justifyContent="normal">
        <Flex height="10vh">
          <Menu />
          <ColorModeSwitcher justifySelf="flex-end" />
        </Flex>
        <Box height="90vh" overflowY="scroll">
          <Hello />
          <About />
        </Box>
        <ColoredButtons />
      </Box >
    </ChakraProvider >
  );
}

export default App;

import React, { useState } from 'react';
import { Button, VStack } from '@chakra-ui/react';

function ColoredButtons() {
  const [selectedButton, setSelectedButton] = useState(null);

  const handleClick = (buttonName) => {
    setSelectedButton(buttonName);
  };

  return (
    <VStack spacing={4}>
      <Button
        colorScheme={selectedButton === 'Button 1' ? 'teal' : 'gray'}
        onClick={() => handleClick('Button 1')}
      >
        Button 1
      </Button>
      <Button
        colorScheme={selectedButton === 'Button 2' ? 'teal' : 'gray'}
        onClick={() => handleClick('Button 2')}
      >
        Button 2
      </Button>
      <Button
        colorScheme={selectedButton === 'Button 3' ? 'teal' : 'gray'}
        onClick={() => handleClick('Button 3')}
      >
        Button 3
      </Button>
    </VStack>
  );
}
